  //   const body = await response.json();
  //   console.log(body);
  //   if (response.status !== 200) throw Error(body.message);

  //   return body;
  // };

  render() {
    return (
      <MuiThemeProvider theme={theme}>
        <Router>
          <Security
            issuer={config.issuer}
            client_id={config.client_id}
            redirect_uri={config.redirect_uri}
            onAuthRequired={this.onAuthRequired}
          >
            <Navbar />

            <Switch>
              <Route
                exact
                path="/twitter"
                component={() => (window.location = "http://twitter.com")}
              />
              <Route
                exact
                path="/github"
                component={() => (window.location = "http://github.com")}
              />
              <Route exact path="/" component={HomePage} />
            </Switch>
            <Route
              path="/(.+)"
              render={() => (
                <div>
                  <Switch>
                    <Route
                      path="/login"
                      render={() => (
                        <Login baseUrl="https://dev-500578.oktapreview.com" />
                      )}
                    />
                    <Route
                      path="/implicit/callback"
                      component={ImplicitCallback}
                    />
                    <Route path="/portal" component={WebPortal} />
                    <Route
                      path="/github"
                      component={() =>
                        (window.location = "https://github.com/login")
                      }
                    />
                    <Route path="/news" component={NewsPage} />
                    <Route
                      exact
                      path="/history/:id(\d+)"
                      render={props => {
                        const id = props.match.params.id;
                        return <HistoryPage id={id} />;
                      }}
                    />
                    <Route
                      exact
                      path="/:id(\d+)"
                      render={props => {
                        const id = props.match.params.id;
                        return <HomePage id={id} />;
                      }}
                    />
                  </Switch>
                </div>
              )}
            />
          </Security>
        </Router>
      </MuiThemeProvider>
    );
  }
}

export default App;
